const sequelize = require('../config/database');

export const register = (req,res) => {

    //Checking existing user
    const q = "SELECT * FROM users WHERE email = ? OR username = ?";

    sequelize.query(q, [req.body.email, req.body.name], (err,data) => {
        if(err) return res.json(err);
        if(data.length) return res.status(409).json("User already exist!");

        //Hash
        const salt = bcrypt.genSaltSync(10);
        const hash = bcryps.hashsync(req.body.password, salt);

        const q = "INSERT INTO users (`username`, `email`, `password`) VALUES (?)" 

        const values = [
            req.body.username,
            req.body.email,
            hash,
        ]

        sequelize.query(q, [values], (err,data) => {
            if(err) return res.json(err);
            return res.status(200).json("Users has been added succesfully");
        })
    })
}

export const login = (req, res) => {
    //CHECK USER
  
    const q = "SELECT * FROM users WHERE username = ?";
  
    sequelize.query(q, [req.body.username], (err, data) => {
      if (err) return res.status(500).json(err);
      if (data.length === 0) return res.status(404).json("User not found!");
  
      //Check password
      const isPasswordCorrect = bcrypt.compareSync(
        req.body.password,
        data[0].password
      );
  
      if (!isPasswordCorrect)
        return res.status(400).json("Wrong username or password!");
  
      const token = jwt.sign({ id: data[0].id }, "jwtkey");
      const { password, ...other } = data[0];
  
      res
        .cookie("access_token", token, {
          httpOnly: true,
        })
        .status(200)
        .json(other);
    });
  };

  export const logout = (req, res) => {
    res.clearCookie("access_token",{
      sameSite:"none",
      secure:true
    }).status(200).json("User has been logged out.")
  };

