sourcesContent:
const nodeMailer = require("nodemailer");

const adminEmail = "didi.aryadi2002@gmail.com";
const adminPassword = "";

//gmail
const mailHost = "smtp.gmail.com";

//Port 587 for gmail
const mailPort = 587;

const sendMail = async (to, subject, htmlContent) => {
  const transporter = nodeMailer.createTransport({
    host: mailHost,
    port: mailPort,
    secure: false,
    auth: {
      user: adminEmail,
      pass: adminPassword,
    },
  });

  const info = await transporter.sendMail({
    from: adminEmail,
    to: to,
    subject: subject,
    html: htmlContent,
  });

  return info;
};

module.exports = {
  sendMail,
};

